class Service {
    constructor(config) {
        this._urls = {};
    }
    add(key, value) {
        this._urls[key] = value;
    }
    addList(list) {
        for(let item of list) {
            let url = process.env.API_GATEWAY.endsWith("/") ? process.env.API_GATEWAY : process.env.API_GATEWAY + "/";
            url = url + item[0] + "/";
            if(process.env.API_VERSION) {
                url = url + process.env.API_VERSION + "/";
            }
            url = url + item[1]; 
            this.add(item[1], url);
        }
    }   
    get(key) {
        let url = this._urls[key];
        return url;
    }
}

module.exports = Service;